note_models.css:
::-webkit-scrollbar {
    display: none;
}

.night_mode {background-color: black;} /*  a simple way to stop inverted colours on ankidroid*/

.card {
color:black!important;
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	display: -webkit-box;
	-webkit-box-align: stretch;
	-webkit-box-pack: center;
	-webkit-box-orient: vertical;
	font-size:22px;
    text-align: center; /* ALIGN TEXT */
}

body {
margin: 0
}


.besogo-comment > input {
    display: none;
}

.besogo-container {
	margin:auto;
    outline: none;
    display: flex;
    background: #d0fefe;
}

.besogo-container * {
    box-sizing: border-box;

}

.besogo-container button {
    height: 30px;
    width: 30px;
    padding: 0px;
    background: #d8dcd6;
}

.besogo-container input {
    font-size: 12pt;
}

.besogo-container input[type='button'] {
    background: #d8dcd6;
}

.besogo-container input[type='text'] {
    width: 30px;
}

.besogo-container svg { /* Prevent text selection within SVG */
    -webkit-touch-callout: none; /* iOS Safari */
    -webkit-user-select: none;   /* Chrome/Safari/Opera */
    -moz-user-select: none;      /* Firefox */
    -ms-user-select: none;       /* Internet Explorer/Edge */
     user-select: none;
}


.besogo-panels {
    flex-direction: column;
    align-items: center;
    padding: 0px 5px;
}

.besogo-panels > * {
    margin: 3px 0px;
}

.besogo-control {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    justify-content: space-around;
    width: 100%;

    flex: 0 0 auto;
}

.besogo-names {
    flex: 0 0 auto;
    width: 100%;
}

.besogo-playerInfo {
    display: flex;
    font-family: Tahoma, Geneva, sans-serif;
    white-space: pre-wrap;
}

.besogo-blackInfo {
    flex: 50%;
    color: white;
    background: black;
    text-align: right;
    padding: 5px;
}

.besogo-whiteInfo {
    flex: 50%;
    color: black;
    background: white;
    text-align: left;
    padding: 5px;
}

.besogo-blackCaps {
    float: left;
}

.besogo-whiteCaps {
    float: right;
}

.besogo-comment {
    font-family: Georgia, serif;
    font-size: 12pt;
    white-space: pre-wrap;


    overflow: auto;
    flex: 1 1 60%;
    width: 100%;
}

.besogo-comment input[type='button'] {
    float: right;
}

.besogo-comment textarea {
    height: 100%;
    width: 100%;
}

.besogo-gameInfo {
    padding: 5px;
    border-collapse: collapse;
}

.besogo-gameInfo td, tr {
    border: 1px solid black;
    padding: 3px;
}

.besogo-tool {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    justify-content: space-around;
    width: 100%;

    flex: 0 0 auto;
}

.besogo-tree {
    overflow: auto;
    flex: 1 1 40%;
    width: 100%;
}

.besogo-file {
	display:none;
    flex: 0 0 auto;
}


/* Kaya board theme */

.besogo-svg-board {
    fill: none;
}
.besogo-svg-backer {
    fill: #fcbc5b;
}
.besogo-board {
    background: url("_shinkaya4.jpg");
    background-size: cover;
}

.besogo-svg-lines {
    stroke: #151515;
    stroke-width: 2.5;
    fill: none;
}

.besogo-svg-hoshi {
    stroke: #151515;
    stroke-width: 12;
}

.besogo-svg-blackStone {
    fill: #252525;
    stroke: #252525;
    stroke-width: 4;
}

.besogo-svg-whiteStone {
    fill: #f5f5f5;
    stroke: #f5f5f5;
    stroke-width: 4;
}

.besogo-svg-greyStone {
    fill: #929591;
    stroke: #929591;
    stroke-width: 4;
}
